import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

function createData(part_of_day, monday, tuesday, wednesday, thrusday, friday, saturday, sunday) {
  return { part_of_day, monday, tuesday, wednesday, thrusday, friday, saturday, sunday };
}

const rows = [
  createData('BreakFast','Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Lunch','Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Dinner', 262, 16.0, 24, 6.0),
  createData('Night', 305, 3.7, 67, 4.3),
];


async function getPatientData(){

  console.log("Chamei funÃ§ao getPatientData");

  localStorage.setItem("patient_id", 1);
  let patient_id = localStorage.getItem("patient_id");

  let url = "http://localhost:9000/patients/" + patient_id;

  let data = await fetch(url, {
    method: 'GET',
    headers: new Headers({
      'Content-Type': 'application/json',
      'Authorization': localStorage.getItem('token')
    })
  });


  let result = {};

  let response = await data.json();

  result["pills_not_took"] = JSON.parse(response.message.pills_not_took);
  result["treatment"] = JSON.parse(response.message.treatment);
  result["total_pills_not_took"]  = result["pills_not_took"].length;
  result["total_pills_took"] = JSON.parse(response.message.total_pills_took);

  console.log(result);
  return result;
}

export default function BasicTable() {

  const classes = useStyles();

  let data;

  const my_rows = [];

  getPatientData().then((res)=>{
    data = res;
    console.log(data["treatment"]);


    let parts_of_day = ["BreakFast", "Lunch", "Dinner", "Night"];

    for(var i = 0; i < 4; i++){

      let parameters = [parts_of_day[i], "", "", "", "", "", "", ""];

      data["treatment"].map((p)=>{
        if(p.part_of_day == i + 1){
          parameters[p.day] = parameters[p.day] + "; "+  p.quantity + " " +p.name+" pills"
          //console.log( (i+1) + " -> " + p.part_of_day + " -> " + p.day);
        }
      });

      my_rows.push(createData(parameters[0], parameters[1],parameters[2],parameters[3],parameters[4],parameters[5],parameters[6], parameters[7], parameters[8]))

    }


  });






  return (
    <>
        <div className="content">
            <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="simple table">
                <TableHead>
                <TableRow>
                    <TableCell></TableCell>
                    <TableCell align="right">Monday</TableCell>
                    <TableCell align="right">Tuesday</TableCell>
                    <TableCell align="right">Wednesday</TableCell>
                    <TableCell align="right">Thursday</TableCell>
                    <TableCell align="right">Friday</TableCell>
                    <TableCell align="right">Saturday</TableCell>
                    <TableCell align="right">Sunday</TableCell>
                </TableRow>
                </TableHead>
                <TableBody>
                {my_rows.map((row) => (
                    <TableRow key={row.part_of_day}>
                    <TableCell >
                        {row.part_of_day}
                    </TableCell>
                    <TableCell align="right">{row.monday}</TableCell>
                    <TableCell align="right">{row.tuesday}</TableCell>
                    <TableCell align="right">{row.wednesday}</TableCell>
                    <TableCell align="right">{row.thrusday}</TableCell>
                    <TableCell align="right">{row.friday}</TableCell>
                    <TableCell align="right">{row.saturday}</TableCell>
                    <TableCell align="right">{row.sunday}</TableCell>
                    </TableRow>
                ))}
                </TableBody>
            </Table>
            </TableContainer>
        </div>
    </>
  );
}
