import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Line } from "react-chartjs-2";
import { UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  CardTitle,
  Form,
  Input,
  Button,
  Row,
  Col,
} from "reactstrap";

export default class NewPatient extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
          my_rows: [1],
          name: "",
          age: "",
          pills : []
      }

      this.handleSubmit = this.handleSubmit.bind(this);
      this.addRow = this.addRow.bind(this);
      this.removeRow = this.removeRow.bind(this);
      this.createPatient = this.createPatient.bind(this);
    }

    handleSubmit(e){
        e.preventDefault();
        console.log("cliquei");
    }

    addRow(){
        let rows = this.state.my_rows;
        rows.push(1);

        this.setState({
            my_rows: rows
        })
    }

    removeRow(){
        let rows = this.state.my_rows;
        rows.pop();
        this.setState({
            my_rows: rows
        })
    }

    createPatient(){
        console.log(this.state.name)
    }


    render() {

        return (
            <>
                <div className="content">
                    <Row>
                        <Col md="3"  form >
                            {/* UserName */}
                            <h4> Information about Patient </h4>
                                <label htmlFor="feFirstName" style={{marginTop: "5%"}}>Name</label>
                                <Input
                                    id="feFirstName"
                                    placeholder=""
                                    name="name"
                                    value={this.state.name}
                                    onChange={(e)=>setUsername(e.target.value)} 
                                />

                            {/* PassWord */}

                                <label htmlFor="feLastName" style={{marginTop:"30px"}}>Age</label>
                                <Input
                                    id="feLastName"
                                    placeholder=""
                                    type="number"
                                    name="age"
                                    onChange={ (e)=>setState({ name: e.target.value}) }
                                />



                            <Button theme="accent"  style={{marginLeft: "70px", marginTop:"30px"}} onClick={this.createPatient}>Create</Button>

                        </Col>
                        <Col  md="4" style={{marginLeft:"10%"}}>

                        <h4>Treatment - Pills </h4> <br></br>
                            {this.state.my_rows.map( (row) => {

                                return (
                                    <span>
                                        <label >Name</label>
                                        <Input
                                        id="feLastName"
                                        placeholder=""
                                        type="text"
                                        name="age"
                                        /*onChange={ (e)=>setState({ name: e.target.value}) }*/
                                        />
                                        <label style={{marginTop: "5%"}}>Quantity</label>
                                        <Input
                                        id="feLastName"
                                        placeholder=""
                                        type="number"
                                        name="age"
                                        /*onChange={ (e)=>setPassword(e.target.value) }*/
                                        />
                                        <Row style={{marginBottom: "5%", marginTop: "5%" }}>
                                            <Col>
                                            <label>Day</label> <br></br>
                                            <UncontrolledDropdown group>
                                            <DropdownToggle caret>
                                                Select Day of Week
                                            </DropdownToggle>
                                            <DropdownMenu>
                                                <DropdownItem>Monday</DropdownItem>
                                                <DropdownItem>Tuesday</DropdownItem>
                                                <DropdownItem>Wednesday</DropdownItem>
                                                <DropdownItem>Thrusday</DropdownItem>
                                                <DropdownItem>Friday</DropdownItem>
                                                <DropdownItem>Saturday</DropdownItem>
                                                <DropdownItem>Sunday</DropdownItem>
                                            </DropdownMenu>
                                            </UncontrolledDropdown>
                                            </Col>
                                            <Col>
                                            <label>Part of Day</label> <br></br>
                                            <UncontrolledDropdown group>
                                            <DropdownToggle caret>
                                                Select Part Of Day
                                            </DropdownToggle>
                                            <DropdownMenu>
                                                <DropdownItem>BreakFast</DropdownItem>
                                                <DropdownItem>Lunch</DropdownItem>
                                                <DropdownItem>Dinner</DropdownItem>
                                                <DropdownItem>Night</DropdownItem>
                                            </DropdownMenu>
                                            </UncontrolledDropdown>
                                            </Col>
                                        </Row>


                                    </span>

                                )

                            } )}

                            <Button theme="accent" color="success" style={{ marginLeft: "70%" ,marginTop:"30px"}} onClick={this.addRow}>
                                <i className="nc-icon nc-simple-add" />
                            </Button>
                            <Button theme="accent" color="danger"  style={{marginLeft: "70px", marginTop:"30px"}} onClick={this.removeRow}>
                                <i className="nc-icon nc-box" />
                            </Button>

                        </Col>


                    </Row>
                </div>
            </> 
            )
    }
}
