import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { render } from 'node-sass';


export default class BasicTable extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        my_rows: []
    }
    this.useStyles = this.useStyles.bind(this);
    this.getPatientData = this.getPatientData.bind(this);
    this.createData = this.createData.bind(this)
    this.data = {}

  }


  useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
  });

  createData(part_of_day, monday, tuesday, wednesday, thrusday, friday, saturday, sunday) {
    return { part_of_day, monday, tuesday, wednesday, thrusday, friday, saturday, sunday };
  }



  async cleanData(){

    getPatientData().then((res)=>{
      data = res;


      let parts_of_day = ["BreakFast", "Lunch", "Dinner", "Night"];

      for(var i = 0; i < 4; i++){

        let parameters = [parts_of_day[i], "", "", "", "", "", "", ""];

        data["treatment"].map((p)=>{
          if(p.part_of_day == i + 1){
            parameters[p.day] = parameters[p.day] + "; "+  p.quantity + " " +p.name+" pills"
          }
        });

        this.state.my_rows.push(this.createData(parameters[0], parameters[1],parameters[2],parameters[3],parameters[4],parameters[5],parameters[6], parameters[7], parameters[8]))

      }

    });

  }

  async getPatientData(){

    console.log("Chamei funÃ§ao getPatientData");

    localStorage.setItem("patient_id", 1);
    let patient_id = localStorage.getItem("patient_id");

    let url = "http://localhost:9000/patients/" + patient_id;

    let data = await fetch(url, {
      method: 'GET',
      headers: new Headers({
        'Content-Type': 'application/json',
        'Authorization': localStorage.getItem('token')
      })
    });


    let result = {};

    let response = await data.json();

    result["pills_not_took"] = JSON.parse(response.message.pills_not_took);
    result["treatment"] = JSON.parse(response.message.treatment);
    result["total_pills_not_took"]  = result["pills_not_took"].length;
    result["total_pills_took"] = JSON.parse(response.message.total_pills_took);

    return result;
  };


  render() {
    return (
      <>
          <div className="content">
              <TableContainer component={Paper}>
              <Table className={classes.table} aria-label="simple table">
                  <TableHead>
                  <TableRow>
                      <TableCell></TableCell>
                      <TableCell align="right">Monday</TableCell>
                      <TableCell align="right">Tuesday</TableCell>
                      <TableCell align="right">Wednesday</TableCell>
                      <TableCell align="right">Thursday</TableCell>
                      <TableCell align="right">Friday</TableCell>
                      <TableCell align="right">Saturday</TableCell>
                      <TableCell align="right">Sunday</TableCell>
                  </TableRow>
                  </TableHead>
                  <TableBody>
                  {this.state.my_rows.map((row) => (
                      <TableRow key={row.part_of_day}>
                      <TableCell >
                          {row.part_of_day}
                      </TableCell>
                      <TableCell align="right">{row.monday}</TableCell>
                      <TableCell align="right">{row.tuesday}</TableCell>
                      <TableCell align="right">{row.wednesday}</TableCell>
                      <TableCell align="right">{row.thrusday}</TableCell>
                      <TableCell align="right">{row.friday}</TableCell>
                      <TableCell align="right">{row.saturday}</TableCell>
                      <TableCell align="right">{row.sunday}</TableCell>
                      </TableRow>
                  ))}
                  </TableBody>
              </Table>
              </TableContainer>
          </div>
      </>
    );
  }

}
